$(function(){var pageName=$(document.body).attr('id');switch(pageName){case'home':handleHome();break;default:console.log('该页面暂无 JavaScript 操作！')}$('#btn-nav').on('click',function(){$('#header, #cat-header').toggleClass('show-nav')});$(window).on('scroll',function(){if($(this).scrollTop()>600){$('#go-top').fadeIn(300)}else{$('#go-top').fadeOut(300)}});$('#go-top').on('click',function(event){$('body, html').animate({scrollTop:0},600);event.preventDefault()});function handleHome(){var len=$('#hero-slides .slide').length,slideIndex=1,timer;function slidesAnimation(){$('#hero-slides .slide').each(function(i){$(this).eq(slideIndex).css('left','0%').end().not(slideIndex).css('left',(-(slideIndex-i)*100)+'%');$('.slides-nav a').eq(slideIndex).addClass('current').siblings().removeClass('current')});slideIndex>=len-1?slideIndex=0:slideIndex++}timer=setInterval(slidesAnimation,6000);$('.slides-nav a').each(function(){$(this).on('click',function(event){clearInterval(timer);slideIndex=$(this).index();slidesAnimation();event.preventDefault()})})}});